const entry = $('Webhook').first().json.body.entry[0];
const change = entry.changes[0].value;

if (!change?.messages?.[0]) {
  return [];
}
if (change.messages[0].type == 'text') {
  return [{
    type: change.messages[0].type,
    text_message: change.messages[0].text.body,
    sender: change.contacts[0].wa_id,
    name: change.contacts[0].profile.name,
    phone_number_id: change.metadata.phone_number_id,
    access_token:$input.first().json.access_token
  }];
}
if (change.messages[0].type == 'audio' && change.messages[0].audio.voice == true) {
  return [{
    type: change.messages[0].type,
    audio_message: change.messages[0].audio.sha256,
    sender: change.contacts[0].wa_id,
    name: change.contacts[0].profile.name,
    phone_number_id: change.metadata.phone_number_id,
    media_id: change.messages[0].audio.id,
    access_token:$input.first().json.access_token
  }];
}

const workflowStaticData = $getWorkflowStaticData('global');

// inititate the accessToken if it desn't exist yet
if (!workflowStaticData.hasOwnProperty('accessToken')) {
  workflowStaticData.accessToken = 0
}

// inititate the timestamp if it desn't exist yet
if (!workflowStaticData.hasOwnProperty('timestamp')) {
  workflowStaticData.timestamp = $now.toISO()
}

return [
  {
      // data: $input.all(),
      accessToken: workflowStaticData.accessToken,
      timestamp: workflowStaticData.timestamp,
      // today: $today
  }
];

const workflowStaticData = $getWorkflowStaticData('global');

// get new access token
workflowStaticData.accessToken = $input.first().json.access_token;
// set timestamp of new access token
workflowStaticData.timestamp = $now.toISO();

return [
  {
      accessToken: workflowStaticData.accessToken,
      timestamp: workflowStaticData.timestamp,
  }
];

